        long : this.state.co[value].lo
    })
 }


 return(
     <div>
    <Form>
        <Form.Field>
            <h1>City</h1>
    <Dropdown
      placeholder='Select City'
      fluid
      selection
      onChange={this.handleChange}
      options={cityOptions}
    />
    </Form.Field>
    <Button primary>
          Submit!
        </Button>
    </Form>
    <h3>{this.state.lat}</h3>
    </div>
  )
  
}
}
export default SelectForm
