import React, { Component } from "react";
import { Dropdown } from 'semantic-ui-react'
import Layout from './layout';

class MyComponent extends Component {
  
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        city:"bangalore"
      };
    }
  
    componentDidMount() {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=b5b0475c91ae54e97e596206b2dd0700`)
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result
            });
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }
    handleChange = (e) => {
      e.preventDefault();
      //console.log("handlechange 1st:",this.state.city);
      //console.log(this.textInput.value)
      const newCity = this.textInput.value;
      //console.log("newcity:",newCity)
      //console.log('submitted');
      this.setState({city:newCity})
     // console.log("after change state:",this.state.city)
      
      
   }
  
    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
            // <Drop />
        //   <ul>
        //     {/* {items.map(item => (
        //       <li key={item.name}>
        //         {item.name} {item.price}
        //       </li>
        //     ))} */}
        //     <li>{items.timezone}</li>
        //   </ul>
        <div>
        <form onSubmit={this.handleChange}>
          {console.log("before body:",this.state.city)}
                <input id="input" ref={(input) => { this.textInput = input; }} type="text" placeholder="Enter city Here" />
                <input type="submit" value="Submit" />
                
      </form>
      {console.log("body:",this.state.city)} 
      {console.log(this.state.items)}
      </div>  
        );
      }
    }
  }
export default MyComponent
