import React, { Component } from "react";
import { Dropdown,Form,Button } from 'semantic-ui-react'
import Layout from './layout';

class SelectForm extends Component{
state={
    lat:"lal",
    long:"",
    value:"",
    co:[{
        id:1,
        la:"12.9716",
        lo:"77.5946",
    }]
}

handleChange = (e,{value})=>{
    const {co}=this.state;
    console.log("lat:",this.state.lat);
    console.log(co[0].lat);
   this.setState({
       lat : this.state.co[event.target.value].la,
       long : this.state.co[event.target.value].lo,
   })
   console.log("lattt:",this.state.lat);
}
render(){
const cityOptions = [
    {
      key: 'Bangalore',
      text: 'Bangalore',
      value: 1,
    },
  ]
  
  
 
  const { value } = this.state;

 return(
     <div>
    <Form onSubmit={this.handleChange}>
        <Form.Field>
         <h1>City</h1>
        <Dropdown
        placeholder='Select City'
        fluid
        selection
        options={cityOptions}
        value={value}
        />
        </Form.Field>
        <Button primary>
            Submit!
        </Button>
    </Form>
    <h3>{this.state.lat}</h3>
    
    </div>
  )
  
}
}
export default SelectForm
