import React, { Component } from "react";
import { Dropdown,Grid,Card } from 'semantic-ui-react'
import Layout from './layout';

class MyComponent extends Component {
  
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        city:"bangalore"
      };
    }
  //   componentDidMount() {
    
  //   fetch("https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=a0a0e7eb9508b1beccefd9bd1207f35f")
  //     .then(res => res.json())
  //     .then(
  //       (result) => {
  //         const data = result;
  //         this.setState({
  //           isLoaded: true,
  //           items: data
  //         });
  //       },
  //       // Note: it's important to handle errors here
  //       // instead of a catch() block so that we don't swallow
  //       // exceptions from actual bugs in components.
  //       (error) => {
  //         this.setState({
  //           isLoaded: true,
  //           error
  //         });
  //       }
  //     )
  //     console.log(this.state.items);
  // }
  
    
    handleChange = (e) => {
      e.preventDefault();
      //console.log("handlechange 1st:",this.state.city);
      //console.log(this.textInput.value)
      const newCity = this.textInput.value;
      //console.log("newcity:",newCity)
      //console.log('submitted');
      this.setState({city:newCity})
     // console.log("after change state:",this.state.city)
     fetch(`https://api.openweathermap.org/data/2.5/weather?q=${newCity}&appid=a0a0e7eb9508b1beccefd9bd1207f35f`)
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result
        });
      },
      // Note: it's important to handle errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
     
   }

   renderCards() {


    const item = [
      {
        header: this.items.weather.description,
        meta:  "",
        description:"Weather Condition",
        // style: { overflowWrap: "break-word" },
      },
   
    ];
    return <Card.Group items={items} />;
  }
  
    render() {
      // const { error, isLoaded, items } = this.state;
      // if (error) {
      //   return <div>Error: {error.message}</div>;
      // } else if (!isLoaded) {
      //   return <div>Loading...</div>;
      // } else {
        return (
        <div>
        <form onSubmit={this.handleChange}>
                <input id="input" ref={(input) => { this.textInput = input; }} type="text" placeholder="Enter city Here" />
                <input type="submit" value="Submit" />
                
      </form>
      <h3 style={{marginLeft:"15px", marginRight:"15px"}}>fund Details</h3>
        {/* <Grid stackable style={{marginLeft:"15px", marginRight:"15px"}}>
          <Grid.Row>
            <Grid.Column width={10}>{this.renderCards()}</Grid.Column>
          </Grid.Row>
        </Grid> */}
      {console.log("body:",this.state.city)} 
      {console.log(this.state.items.weather.description)}
      </div>  
        );
      }
    }
  
export default MyComponent
